package main

import (
	"fmt"
)

func canPermutePalindrome(s string) bool {
	sMap := make(map[rune]int)
	for _, v := range s {
		sMap[v]++
	}

	num := 0
	for _, v := range sMap {
		if v%2 != 0 {
			num++
		}
	}

	return num <= 1
}

func test01() {
	s := "tactcoa"
	succResult := true
	fmt.Println("test01 s:=", s)
	fmt.Println("success result:=", succResult)
	fmt.Println("result:=", canPermutePalindrome(s))
	fmt.Println()
}

func test02() {
	s := ""
	succResult := false
	fmt.Println("test02 s:=", s)
	fmt.Println("success result:=", succResult)
	fmt.Println("result:=", canPermutePalindrome(s))
	fmt.Println()
}

func test03() {
	s := "aabbccc"
	succResult := true
	fmt.Println("test03 s:=", s)
	fmt.Println("success result:=", succResult)
	fmt.Println("result:=", canPermutePalindrome(s))
	fmt.Println()
}

func main() {
	test01()
	test02()
	test03()
}
